import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import Chip from "@mui/material/Chip";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { useTheme } from "@mui/material/styles";
import useMediaQuery from "@mui/material/useMediaQuery";
import { createTheme, ThemeProvider } from "@mui/material/styles";

export default function ItemCard() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  const palette = createTheme({
    palette: {
      primary: {
        main: "#365486", // Custom color for primary
      },
    },
  });
  return (
    <Box
      sx={{ display: "flex", flexDirection: "row", justifyContent: "center" }}
    >
      <Card
        sx={{
          position: "relative",
          maxWidth: isMobile ? 300 : 800,
          borderRadius: "8px",
          m: 2,
          ":hover": {
            boxShadow: 20,
          },
          display: "flex",
          flexDirection: "row",
          backgroundColor: "#DCF2F1",
        }}
      >
        <CardMedia
          sx={{
            maxHeight: 300,
            width: 350,
          }}
          image="https://upload.wikimedia.org/wikipedia/commons/c/c4/Chipmunk_with_stuffed_cheeks_in_Prospect_Park_%2805980%29.jpg"
          alt="Product Image"
          title="Listing Photo"
        />
        <CardContent>
          <div
            style={{
              padding: "5px",
              borderRadius: "8px",
              margin: "10px",
              minHeight: "160px",
              maxWidth: "300px",
            }}
          >
            <Typography
              component="div"
              fontFamily="'Helvetica Neue', Helvetica, Arial, sans-serif"
              sx={{
                fontSize: isMobile ? "1rem" : "1.5rem",
                fontWeight: "bold",
                maxWidth: isMobile ? "200px" : "275px",
                display: "block",
                wordWrap: "break-word",
                lineHeight: "1.4",
                mb: "8px",
                color: "#333",
              }}
            >
              Eden Food
            </Typography>

            <Typography
              sx={{
                display: "flex",
                alignContent: "left",
                mb: "12px",
                fontSize: "1.2rem",
                color: "#666",
              }}
              fontFamily="'Helvetica Neue', Helvetica, Arial, sans-serif"
            >
              Created with the aims of solving the issues that farmer in
              singapore face in singapore...
            </Typography>

            <Stack direction="row" spacing={1}>
              <ThemeProvider theme={palette}>
                <Chip label="primary" color="primary" />
              </ThemeProvider>
              <Chip label="success" color="success" />
            </Stack>
            <Typography
              sx={{
                display: "flex",
                alignItems: "center",
                mb: "12px",
                fontSize: "1.2rem",
                color: "#666",
              }}
              fontFamily="'Helvetica Neue', Helvetica, Arial, sans-serif"
            ></Typography>
          </div>
        </CardContent>
      </Card>
    </Box>
  );
}
